command:
while true ; do
  date ;
  curl -sf --tlsv1.2 -X POST \
      --cacert /var/run/secrets/boostport.com/ca.crt \
      -H "X-Vault-Token: $(cat /var/run/secrets/boostport.com/bare-token)" \
      https://vault.cluster.acmedc.net:8200/v1/auth/token/renew-self >/dev/null && \
    echo "Token refreshed" || echo "Failed to refresh token" ;
  sleep 300 ;
done

